var Should = require('should');
var GoogleFeed = require('../lib/index');

var resultsPromise = GoogleFeed.find('moscow');

describe('#find', () => {
  'use strict';

  it('results should be an object', (done) => {
    resultsPromise.then((results) => {
      results.should.be.an.instanceOf(Object);
      done();
    });
  });

  it('results should have `query` property', (done) => {
    resultsPromise.then((results) => {
      results.should.have.property('query');
      done();
    });
  });

  it('entries should be an array', (done) => {
    resultsPromise.then((results) => {
      results.entries.should.be.an.instanceOf(Object);
      done();
    });
  });

  it('entry should be an object', (done) => {
    resultsPromise.then((results) => {
      results.entries[0].should.be.an.instanceOf(Object);
      done();
    });
  });
});
